import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Register(props) {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  function handleEmailInputChange(e) {
    setEmail(e.target.value);
  }

  function handlePasswordInputChange(e) {
    setPassword(e.target.value);
  }

  function handleSubmit(event) {
    event.preventDefault();
    props.onRegisterUser({ email, password });
  }

  return (
    <div className="auth">
      <div className="auth__container">
        <h1 className="auth__heading">Регистрация</h1>
        <form className="auth__form" name="Log-in" noValidate onSubmit={handleSubmit}>
          <input
            className="auth__input"
            type="email"
            name="email"
            placeholder="Email"
            autoComplete="on"
            id="email-input"
            required
            value={email}
            onChange={handleEmailInputChange} />
          <span className="popup__input-error" />
          <input
            className="auth__input"
            type="password"
            name="password"
            placeholder="Пароль"
            autoComplete="on"
            id="password-input"
            required
            value={password}
            onChange={handlePasswordInputChange} />
          <span className="popup__input-error" />
          <button className="auth__save-button" type="submit">Зарегистрироваться</button>
        </form>
        <p className="auth__redirect">Уже зарегистрированы? <Link className="auth__link" to="/sign-in" >Войти</Link></p>
      </div>
    </div>)
}

export default Register;
