import React from 'react';


function InfoTooltip(props) {
  const isSuccess = props.isSuccess
  if (isSuccess) {
    return (
      <div className={`popup popup_type_infotooltip ${props.isOpen && 'popup_opened'}`}>
        <div className="popup__container">
          <h2 className="popup__text">Вы успешно зарегистрировались!</h2>
          <button className="popup__close-button" type="button" aria-label="Закрыть" onClick={props.onClose}></button>
        </div>
      </div>)
  }
  return (
    <div className={`popup popup_type_infotooltip ${props.isOpen && 'popup_opened'}`}>
      <div className="popup__container">
        <h2 className="popup__text">Что-то пошло не так! Попробуйте еще раз.</h2>
        <button className="popup__close-button" type="button" aria-label="Закрыть" onClick={props.onClose}></button>
      </div>
    </div>)
}

export default InfoTooltip;
